import styled from 'styled-components';

export const CartMovie = styled.div`
  display: flex;
`;

export const ImgMovie = styled.img`
  width: 360px;
  margin-right: 20px;
`;

export const TitleMovie = styled.h2`
  font-size: 42px;
  margin: 0;
`;

export const SpanMovie = styled.span`
  font-weight: 500;
`;

export const DescMovie = styled.p`
  font-size: 24px;
  line-height: 2;
`;
